mapper.select:
select * from( select mi.mem_seq, mi.mem_nm, mc.mem_hire_date, de.dtl_cd_nm as department, ps.dtl_cd_nm as position, st.dtl_cd_nm as status, row_number() over (order by mi.mem_seq) as rn from member_info mi join member_company mc on mi.mem_seq = mc.mem_seq left join code_detail de on de.mst_cd='DP01' and mc.dp_cd = de.dtl_cd left join code_detail ps on ps.mst_cd='RA01' and mc.ra_cd = ps.dtl_cd left join code_detail st on st.mst_cd='ST01' and mc.st_cd = st.dtl_cd
and mi.mem_nm like '%' || #{memberName} || '%'
and to_date(mc.mem_hire_date) between to_date(#{firstDate}) and to_date(#{secondDate})
and to_date(mc.mem_hire_date) >= to_date(#{firstDate})
and to_date(mc.mem_hire_date) => to_date(#{secondDate})
and de.dtl_cd = #{departmentCode}
and ps.dtl_cd = #{positionCode}
and st.dtl_cd = #{statusCode}
) where rn between #{startIndex} and #{endIndex}

select count(mi.mem_seq) from member_info mi join member_company mc on mi.mem_seq = mc.mem_seq left join code_detail de on de.mst_cd='DP01' and mc.dp_cd = de.dtl_cd left join code_detail ps on ps.mst_cd='RA01' and mc.ra_cd = ps.dtl_cd left join code_detail st on st.mst_cd='ST01' and mc.st_cd = st.dtl_cd
and mi.mem_nm like '%' || #{memberName} || '%'
and to_date(mc.mem_hire_date) between to_date(#{firstDate}) and to_date(#{secondDate})
and to_date(mc.mem_hire_date) >= to_date(#{firstDate})
and to_date(mc.mem_hire_date) => to_date(#{secondDate})
and de.dtl_cd = #{departmentCode}
and ps.dtl_cd = #{positionCode}
and st.dtl_cd = #{statusCode}

mapper.insert:
insert into member_sk(
	mem_seq,
	sk_cd
)
values(
	#{memberNumber},
	#{skillCode}
)

mapper.select:
select count(dtl_cd)
from code_detail
where mst_cd = 'SK01'
	and dtl_cd in(

		#{skill}

	)

		select
			count(mem_id)
		from member_info
		where
    		mem_seq != #{memberNumber}
    		and mem_id = #{memberId}

mapper.update:
update member_company set dp_cd = #{departmentCode}, ra_cd = #{positionCode},
mem_pic = #{pictureDir},
st_cd = #{statusCode}, mem_hire_date = #{hireDate}, mem_resignation_date = #{resignationDate} where mem_seq = #{memberNumber}

mapper.delete:
delete project_member_table
where mem_seq = #{memberNumber}
	and prj_seq in (

			#{projectNumber}

	)
